mapper.select:
SELECT
*
FROM devops_env_resource dir where 1=1

    AND dir.app_instance_id = #{instanceId}


    AND dir.kind = #{kind}


    AND dir.name = #{name}

SELECT * from devops_env_resource der
LEFT JOIN devops_app_instance dai
on der.app_instance_id = dai.id where dai.env_id = #{envId}
AND der.kind = #{type}
